import React, { useState } from "react";
import { Container, Row, Col } from "shards-react";
import PageTitle from "../components/common/PageTitle";
import MathJax from 'react-mathjax2'
function BlogOverviewdathuc(smallStats) {
  var [result, setResult] = useState(0);
  return (
    <Container fluid className="main-content-container px-4">
      {/* Page Header */}
      <Row noGutters className="page-header py-4">
        <PageTitle title="Đa thức" subtitle="Subtitle" className="text-sm-left mb-3" />
      </Row>
      <div id="guppy1"></div>
      <div style={{
        height: "500px",
        background: "#dbdfe3"
      }}>
        <div style={{
          height: "100%",
          background: "#eef2f6",
          margin: "0 120px 0 120px"
        }}>
          <p id="testdemo"></p>
          <button id="test" >test</button>
          <button onClick={() => {
            var input_latex = document.getElementById('testdemo').innerText;
            fetch("http://127.0.0.1:8000/api/backend_handing", {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({ latex_input: input_latex })
            }).then(res => {
              res.json().then(db => {
                console.log(db);
                setResult(db.result);
              })
            }).catch(err => {
              console.log(err);
              console.log("test1");
            })
          }}>submit</button>

          <MathJax.Context input='tex'
            options={{
              displayAlign: 'left',
              mathmlSpacing: false,
              displayIndent: '0',
              paddingLeft: true,
              skipHtmlTags: [            //  HTML tags that won't be searched for math
                '+'
              ]
            }}
          >
            <div>
              <MathJax.Node>{result}</MathJax.Node>
            </div>
          </MathJax.Context>
        </div>



      </div>
    </Container>
  )
};


export default BlogOverviewdathuc;
